function initAdsElementsRandomView(uniqueElId, adsMobs, adsPC, beforeAds) {
    initAdsElementsRandomViewEl($('#' + uniqueElId), adsMobs, adsPC, beforeAds);
}

const prependAds = '<span><u style="font-size: 15px;">Publicité</u></span><br>';

function initAdsElementsRandomViewEl(uniqueEl, adsMobs, adsPC, beforeAds) {
    const uniqueElIdDiv = uniqueEl;
    if (uniqueElIdDiv.length > 0) {
        //Get Ads count to show.
        let max = Number(uniqueElIdDiv.data('max'));
        let min = Number(uniqueElIdDiv.data('min'));
        let count = randomIntFromInterval(min, max);

        //Don't show ads
        if (count <= 0)
            return;

        //Settings appropriate ads elements.
        let ads = (isMobileDevice) ? adsMobs : adsPC;

        //Nos ads to show
        if (ads.length === 0)
            return;

        //Not enough ads for count needed
        if (ads.length < count)
            count = ads.length;

        //Show all appropriate ads
        if (ads.length === count) {
            for (let i = 0; i < ads.length; i++) {
                uniqueElIdDiv.append(ads[i]);
            }
            if (beforeAds === undefined)
                uniqueElIdDiv.prepend(prependAds);
            return;
        }

        //Choose randoms ads to show
        let randoms = [];

        //console.log(min, max, count, ads);

        while (count > 0) {
            let random = randomIntFromInterval(0, ads.length - 1);

            if (!randoms.includes(random)) {
                randoms.push(random);
                uniqueElIdDiv.append(ads[random]);
                --count;
            }
        }
        if (beforeAds === undefined)
            uniqueElIdDiv.prepend(prependAds);
    }
}

const hrElements = $('#article-content hr');
const pubAfterThis = $('.pub-after-this');

function addAdsToHrInArticle(uniqueEl, adsMobs, adsPC, beforeAds) {
    const uniqueElIdDiv = uniqueEl;
    if (uniqueElIdDiv.length > 0) {
        //Get Ads count to show.
        let count = 1;

        //Settings appropriate ads elements.
        let ads = (isMobileDevice) ? adsMobs : adsPC;

        //Nos ads to show
        if (ads.length === 0)
            return;

        let random = randomIntFromInterval(0, ads.length - 1);
        uniqueElIdDiv.after(ads[random]);

        if (beforeAds === undefined)
            uniqueElIdDiv.after(prependAds);
        else
            uniqueElIdDiv.after(beforeAds);

    }
}

function adsInArticleInitAndPage() {
    hrElements.each(function () {
        addAdsToHrInArticle($(this), adsAmazonBeforeArticleMobs, adsAmazonHorizontalIndexPC);
    });
    pubAfterThis.each(function () {
        addAdsToHrInArticle($(this), adsAmazonBeforeArticleMobs, adsAmazonHorizontalIndexPC);
    });
}

