function initSizeDeviceAds() {
    //Inits adds
    const adsPC = $('.ads-pc');
    const pc = $('.only-pc');
    const adsMob = $('.ads-mob');
    const mob = $('.only-mob');

    if (isMobileDevice) {
        hiddenEl(adsMob, false);
        hiddenEl(mob, false);
        hiddenEl(adsPC, true);
        hiddenEl(pc, true);
        adsMob.show();
        mob.show();
        adsPC.hide();
        pc.hide();
    } else {
        hiddenEl(adsMob, true);
        hiddenEl(mob, true);
        hiddenEl(adsPC, false);
        hiddenEl(pc, false);
        adsMob.hide();
        mob.hide();
        adsPC.show();
        pc.show();
    }
}

